iv><script type="text/javascript">	$(document).ready(function () {				var theHREF;				$(".confirmModalLink").click(function(e) {			e.preventDefault();			theHREF = $(this).attr("href");			$("#confirmModal").modal("show");		});				$("#confirmModalNo").click(function(e) {			$("#confirmModal").modal("hide");		});				$("#confirmModalYes").click(function(e) {			window.location.href = theHREF;		});			});</script>