<?php    namespace Library;    	/**	* Email sender.	*/    class PDOFactory {		/**		* Getter de la connexion à la BDD.		* @return connexion à la BDD		*/        public static function getMysqlConnexion() {            $db = new \PDO('mysql:host=localhost;dbname=t_rider', 'root', '');            $db->setAttribute(\PDO::ATTR_ERRMODE, \PDO::ERRMODE_EXCEPTION);                        return $db;        }    }