<?php  namespace Library\Entities;    class Genre     extends \Library\Entity   {      const LIBELLE_INVALIDE = 1;        protected $libelle;     public function __construct(array $attributs = array())     {      foreach ($attributs as $attribut => $value)       {        $method = 'set'.ucfirst($attribut);                if (is_callable(array($this, $method)))         {          $this->$method($value);        }      }		    }      public function isValid()     {      return !(empty($this->libelle));    }          // SETTERS //         public function setLibelle($libelle)     {      if (empty($libelle))       {        $this->erreurs[] = self::LIBELLE_INVALIDE;      }           else       {        $this->libelle = $libelle;      }    }       // GETTERS //    public function libelle()     {      return $this->libelle;    }      }