<?php    namespace Library\Models;		use \Library\Entities\Utilisateur;        class ConnexionManager_PDO extends ConnexionManager {        /**		* {inheritDoc}		*/		public function connexion($login, $password, \Applications\Backend\BackendApplication $app) {			$requete = $this->dao->prepare('SELECT UTI_ID as id FROM UTILISATEUR WHERE UTI_LOGIN = :login AND UTI_PASSWORD = :password');						$requete->bindValue(':login', $login);			$requete->bindValue(':password', $password);            $requete->execute();						if($utilisateur = $requete->fetch(\PDO::FETCH_ASSOC)) {				$myUser = new Utilisateur($utilisateur);				$app->user()->setAttribute('id', $myUser->id());								return true;			}						return false;			}    }