<?php  namespace Library\Models;		use \Library\Entities\Genre;      abstract class GenreManager     extends \Library\Manager   {				public function save(Genre $genre)     {					if($genre->isValid())       {				$genre->isNew() ? $this->add($genre) : $this->modify($genre);			}			else       {				throw new \RuntimeException('Le genre doit être valide pour être enregistré');			}		}				abstract protected function getGenresList();				abstract protected function add(Genre $genre);				abstract protected function modify(Genre $genre);				abstract protected function delete($id);				abstract protected function getGenreById($id);      }