<?php    namespace Library\Models;		use \Library\Entities\Utilisateur;        abstract class UtilisateurManager extends \Library\Manager {        public function save(Utilisateur $utilisateur) {			if($utilisateur->isValid()) {				$utilisateur->isNew() ? $this->add($utilisateur) : $this->modify($utilisateur);			}			else {				throw new \RuntimeException('L\'utilisateur doit être valide pour être enregistré');			}		}		        abstract protected function modify(Utilisateur $utilisateur);		        abstract protected function getUtilisateurById($id);				abstract protected function getUtilisateurListBySessionId($id);    }