<?php    namespace Library;    	/**	* Classe implémentant de formatter les URL.	*/    class URLFormatter {		/**		* Getter de la connexion à la BDD.		* @return connexion à la BDD		*/        public static function format($libelle) {			//Espace avant et après						$libelle = trim($libelle);						//Accent			$a = 'ÀÁÂÃÄÅÆÇÈÉÊËÌÍÎÏÐÑÒÓÔÕÖØÙÚÛÜÝÞßàáâãäåæçèéêëìíîïðñòóôõöøùúûýýþÿŔŕ';			$b = 'aaaaaaaceeeeiiiidnoooooouuuuybsaaaaaaaceeeeiiiidnoooooouuuyybyRr';			$libelle = utf8_decode($libelle);			$libelle = strtr($libelle, utf8_decode($a), $b); 						//Espace avant et après			$libelle = trim($libelle);					//On enlève les chiffres			$libelle = preg_replace('#[0-9]#', '', $libelle);						//Espace avant et après			$libelle = trim($libelle);						//On enlève les caractères spéciaux			$libelle = preg_replace('#[^a-zA-Z ]#', '', $libelle);						//Espace avant et après			$libelle = trim($libelle);						//On enlève les espaces			$libelle = preg_replace('/\s+/', '-', $libelle);                        return strtolower($libelle);        }    }